import base64
import time
from io import BytesIO

import pyautogui as ui
from PIL import Image

from llmdriver.llmdriver import LLMAutomator


class ChatGPTAutomator(LLMAutomator):
    inputbox_data_uri = 'data:image/png;base64,'
    copybtn_data_uri = 'data:image/png;base64,'
    busysign_data_uri = 'data:image/png;base64,'
    scrolldown_data_uri = 'data:image/png;base64,'

    def __init__(self, silent: bool = False):
        super().__init__(silent=silent)
        self.scrolldown = Image.open(BytesIO(base64.b64decode(self.scrolldown_data_uri.split(",")[1])))

    def is_response_ready(self) -> bool:
        """Check if the response is ready by confirming the absence of the busy sign."""
        try:
            pos = ui.locateCenterOnScreen(self.busysign, confidence=0.99)
            return False
        except ui.ImageNotFoundException:
            pass
        try:
            while pos := ui.locateCenterOnScreen(self.scrolldown, confidence=0.8):
                ui.click(pos.x // 2, pos.y // 2)
                time.sleep(2)
        except ui.ImageNotFoundException:
            pass
        return True